						var widthContainer = $container.find('.slider > div').width(),
							widthScroll = $container.find('.slider > div > div').width(),
							scrollLeft = $container.find('.slider > div').scrollLeft();
						self.showArrows(widthContainer < widthScroll);
						self.nextAllow(widthScroll - scrollLeft > widthContainer);
						self.prevAllow(scrollLeft > 0);
					};

					// Check scoll change - 15 fps
					setInterval(function () {
